export const pattern = '[^@]+@[^@]+\.[a-zA-Z]{2,6}';

export const cardsTable = document.querySelectorAll('.cards');
export const shortMovieDuration = 40;
export const firstMovies = {
    large: 16,
    medium: 12,
    small: 8,
    smallest: 5,
};
export const nextStep = {
    large: 4,
    medium: 3,
    small: 2,
};
